theme-form.Template:
{% assign SNIdx = nil %}
{% assign itemIdx = nil %}
{% assign descriptionIdx = nil %}
{% assign qtyIdx = nil %}
{% assign unitPriceIdx = nil %}
{% assign priceIdx = nil %}
{% assign discountIdx = nil %}
{% assign amountIdx = nil %}
{% assign taxIdx = nil %}
{% assign taxAmountIdx = nil %}
{% assign totalIdx = nil %}
{% for column in table.columns %}
    {% if column.label == '#' %}
        {% assign SNIdx = forloop.index0 %}
    {% elsif column.label == 'Item' %}
        {% assign itemIdx = forloop.index0 %}
    {% elsif column.label == 'Description' %}
        {% assign descriptionIdx = forloop.index0 %}
    {% elsif column.label == 'Unit price' %}
        {% assign unitPriceIdx = forloop.index0 %}
        {% assign qtyIdx = forloop.index0 | minus: 1 %}
    {% elsif column.label == 'Price' %}
        {% assign priceIdx = forloop.index0 %}
    {% elsif column.label == 'Discount' %}
        {% assign discountIdx = forloop.index0 %}
    {% elsif column.label == 'Amount' %}
        {% assign amountIdx = forloop.index0 %}
    {% elsif column.label == 'Tax' %}
        {% assign taxIdx = forloop.index0 %}
    {% elsif column.label == 'Tax Amount' %}
        {% assign taxAmountIdx = forloop.index0 %}
    {% elsif column.label == 'Total' %}
        {% assign totalIdx = forloop.index0 %}
    {% endif %}
{% endfor %}
{% assign full_url = custom_fields['Receipt QRCode'] %}
{% if full_url | contains: '/' %}
    {% assign url_parts = full_url | split: '/' %}
    {% assign base_url = url_parts[0] | append: '//' | append: url_parts[1] %}
{% endif %}
<table style='width: 100%; padding: 20px; font-family: Arial, sans-serif;'>
    <tr>
        <td colspan='3' style='padding-bottom: 10px;'>
            <table style='width: 100%;'>
                <tr>
                    <td style='width: 280px; vertical-align: top;'>
                        <div>
                            <img src='{{ business.logo }}' style='width: 100px;' />
                        </div>
                        <h1 style='margin: 0; font-size: 24px; text-align: left; padding: 0px; color: black; text-shadow: 2px 2px 4px #aaa;'>{{ title }}</h1>
                    </td>
                    <td style='text-align: right; padding-right: 20px; vertical-align: top;'>
                        <div style='font-size: 12px; margin-top: 20px;'>
                            Verification code<br/>
                            {{ custom_fields['Verification Code'] }}
                        </div>
                        <div style='font-size: 12px; margin-top: 10px;'>
                            Verify this receipt manually at<br/>
                            <a href='{{ full_url }}' target='_blank'>{{ base_url }}</a>
                        </div>
                    </td>
                    <td style='width: 96px; vertical-align: top;'>
                        <div style='font-size: 12px;'> </div>
                        <div style='width: 94px; height: 94px; border: 1px solid #dddddd; display: flex; justify-content: center; align-items: center;'>
                            <div id='zimraqrcode' style='width: 80px; height: 80px; text-align: center'>{{ custom_fields['Approval Status'] }}</div>
                        </div>
                        {% assign isApproved = custom_fields['Approval Status'] %}
                        {% if isApproved == 'APPROVED' %}
                        <script src='resources/qrcode/qrcode.js'></script>
                        <script>
                            const qrCodeElement = document.getElementById('zimraqrcode');
                            if (qrCodeElement) {
                                const qrCodeText = '{{ full_url }}'; 
                                qrCodeElement.innerHTML = '';
                                new QRCode(qrCodeElement, {text: qrCodeText, width: 80, height: 80, colorDark: '#000000', colorLight: '#ffffff', correctLevel: QRCode.CorrectLevel.L});
                            } 
                        </script>
                        {% endif %}
                    </td>
                </tr>
            </table>
        </td>
    </tr>
    <tr>
        <td style='width: 50%; padding: 8px; vertical-align: top;'>
            {{ business.address }}
        </td>
        <td style='width: 50%; padding: 8px; vertical-align: top;'><div>
            <div style='font-weight: bold; color: black; text-shadow: 2px 2px 4px #aaa;'>BUYER</div>
            <div style='font-weight: bold;'>{{ custom_fields['Buyer Register Name'] }}</div>
            <div>{{ custom_fields['Buyer Trade Name'] }}</div>
            <div>TIN: {{ custom_fields['Buyer TIN'] }} — Vat No: {{ custom_fields['Buyer Vat Number'] }}</div>
            <div>{{ custom_fields['Buyer Province'] }} — {{ custom_fields['Buyer City'] }} <br />{{ custom_fields['Buyer Street'] }} — {{ custom_fields['Buyer House No'] }}</div>
            <div>Email: {{ custom_fields['Buyer Email'] }}</div>
            <div>Phone No: {{ custom_fields['Buyer Phone No'] }}</div></div>
        </td>
    </tr>
    {% assign title_lower = title | downcase %}
    {% assign IssueDate = fields['Issue date'] %}
    {% assign Reference = fields['Reference'] %}
    {% assign Invoice = fields['Invoice'] %}
    {% if title_lower contains 'invoice' %}
        {% assign IssueDate = fields['Invoice date'] %}
        {% assign Reference = fields['Invoice number'] %}
        {% assign Invoice = '' %}
    {% endif %}
    <tr>
        <td colspan='2' style='padding-top: 10px;'>
            <table style='width: 100%; border-collapse: collapse; margin-bottom: 10px; background-color: #f8f9fa;'>
                <tr>
                    <td style='width: 33%; padding: 8px; text-align: left;'>
                        <div>Customer reference No: <b>{{ Reference }}</b></div>
                        <div>Date: <b>{{ custom_fields['Receipt Date'] }}</b></div>
                    </td>
                    <td style='width: 34%; padding: 8px; text-align: left;'>
                        <div>Invoice No: <b>{{ custom_fields['Receipt Counter'] }} / {{ custom_fields['Receipt GlobalNo'] }}</b></div>
                        <div>Fiscal day No: <b>{{ custom_fields['FiscalDayNo'] }}</b></div>
                    </td>
                    <td style='width: 33%; padding: 8px; text-align: left;'>
                       <div>Fiscal device ID: <b>{{ custom_fields['DeviceID'] }}</b></div> 
                       <div>Device Serial No: <b>{{ custom_fields['DeviceSN'] }}</b></div>
                    </td>
                </tr>
            </table>
        </td>
    </tr>
    {% unless title_lower contains 'invoice' %}
    <tr>
        <td colspan='2'>
            <div style='margin-bottom: 10px;'>
                <div style='font-weight: bold; margin-bottom: 10px; text-align: center !important; color: black; text-shadow: 2px 2px 4px #aaa;'>{% if title_lower contains 'credit' %}CREDITED INVOICE{% else %}DEBITED INVOICE{% endif %}</div>
                <table style='width: 100%; border-collapse: collapse; background-color: #f8f9fa;'>
                    <tr>
                        <td style='width: 50%; padding: 8px; text-align: left;'>
                            <div>Customer reference No: <b>{{ custom_fields['Receipt NoRef'] }}</b></div>
                            <div>Date: <b>{{ custom_fields['Receipt DateRef'] }}</b></div>
                        </td>
                        <td style='width: 50%; padding: 8px; text-align: left;'>
                            <div>Invoice No: <b>{{ custom_fields['Receipt CounterRef'] }} / {{ custom_fields['Receipt GlobalNoRef'] }}</b></div>
                            <div>Device Serial No: <b>{{ custom_fields['DeviceSN Ref'] }}</b></div>
                        </td>
                    </tr>
                </table>
            </div>
        </td>
    </tr>
    {% endunless %}
    <tr>
        <td colspan='2'>
            <table style='width: 100%'>
        		<thead>
        		    {% if description %} <tr><td colspan='99'><div style='font-size: 12px; font-weight: bold; margin-bottom: 10px'>{{ description }}</div></td></tr>{% endif %} 
        			<tr>
        				{% if SNIdx != nil %}
        				<th style='padding: 5px 10px; text-align: left; vertical-align: middle; border-top: 1px solid #dddddd; border-bottom: 1px solid #dddddd; background-color: #f8f9fa;'>#</th>
        				{% endif %}
        				<th style='padding: 5px 10px; text-align: left; vertical-align: middle; border-top: 1px solid #dddddd; border-bottom: 1px solid #dddddd; background-color: #f8f9fa;'>Description</th>
        				<th style='padding: 5px 10px; text-align: right; vertical-align: middle; border-top: 1px solid #dddddd; border-bottom: 1px solid #dddddd; background-color: #f8f9fa; width: 80px;'>Qty</th>
        				<th style='padding: 5px 10px; text-align: right; vertical-align: middle; border-top: 1px solid #dddddd; border-bottom: 1px solid #dddddd; background-color: #f8f9fa; width: 80px;'>Unit Price</th>
        				{% if amountIdx != nil %}
        				<th style='padding: 5px 10px; text-align: right; vertical-align: middle; border-top: 1px solid #dddddd; border-bottom: 1px solid #dddddd; background-color: #f8f9fa; width: 80px;'>Amount<br/>(excl. VAT)</th>
        				{% endif %}
        				<th style='padding: 5px 10px; text-align: right; vertical-align: middle; border-top: 1px solid #dddddd; border-bottom: 1px solid #dddddd; background-color: #f8f9fa; width: 50px;'>VAT</th>
        				<th style='padding: 5px 10px; text-align: right; vertical-align: middle; border-top: 1px solid #dddddd; border-bottom: 1px solid #dddddd; background-color: #f8f9fa; width: 100px;'>Amount<br/>(incl. VAT)</th>
        			</tr>
        		</thead>
        		<tbody>
                    {% for row in table.rows %}
                        {% assign SNText = nil %}
                        {% assign itemText = nil %}
                        {% assign descriptionText = nil %}
                        {% assign qty = nil %}
                        {% assign qtyText = nil %}
                        {% assign unitPrice = nil %}
                        {% assign unitPriceText = nil %}
                        {% assign price = 0 %}
                        {% assign priceText = nil %}
                        {% assign discount = 0 %}
                        {% assign discountText = nil %}
                        {% assign amount = 0 %}
                        {% assign amountText = nil %}
                        {% assign taxText = ' ' %}
                        {% assign taxAmount = nil %}
                        {% assign taxAmountText = ' ' %}
                        {% assign total = 0 %}
                        {% assign totalText = nil %}
                        {% for cell in row.cells %}
                            {% if forloop.index0 == SNIdx %}
                                {% assign SNText = cell.text %}
                            {% elsif forloop.index0 == itemIdx %}
                                {% assign itemText = cell.text | strip %}
                            {% elsif forloop.index0 == descriptionIdx %}
                                {% assign descriptionText = cell.text | strip %}
                            {% elsif forloop.index0 == qtyIdx %}
                                {% assign qty = cell.value %}
                                {% assign qtyText = cell.value %}
                            {% elsif forloop.index0 == unitPriceIdx %}
                                {% assign unitPrice = cell.value %}
                                {% assign unitPriceText = cell.text %}
                            {% elsif forloop.index0 == priceIdx %}
                                {% assign price = cell.value %}
                                {% assign priceText = cell.text %}
                            {% elsif forloop.index0 == discountIdx %}
                                {% assign discount = cell.value %}
                                {% assign discountText = cell.text %}
                            {% elsif forloop.index0 == amountIdx %}
                                {% assign amount = cell.value %}
                                {% assign amountText = cell.text %}
                            {% elsif forloop.index0 == taxIdx %}
                                {% assign taxText = cell.text %}
                            {% elsif forloop.index0 == taxAmountIdx %}
                                {% assign taxAmount = cell.value %}
                                {% assign taxAmountText = cell.text %}
                            {% elsif forloop.index0 == totalIdx %}
                                {% assign total = cell.value %}
                                {% assign totalText = cell.text %}
                            {% endif %}
                            {% if descriptionText == nil or descriptionText == ' ' or descriptionText == '' %}
                                {% assign descriptionText = itemText %}
                            {% endif %}
                        {% endfor %}
                        {% if taxText == 'Exempt' or taxText == ' ' or taxText == '' %}
                            {% assign taxAmountText = '0' %}
                            {% assign taxAmount = 0 %}
                        {% elsif taxText == 'VAT (0%)' %}
                            {% assign taxAmountText = '0.00' %}
                        {% endif %}
                        {% if amount == 0 %}
                            {% assign amountText = totalText %}
                        {% endif %}
                        <tr>
                            {% if SNIdx != nil %}
                            <td style='padding: 5px 10px; text-align: left; vertical-align: top; width: 50px;'>{{ SNText }}</td>
                            {% endif %}
                            <td style='padding: 5px 10px; text-align: left; vertical-align: top;'>{{ descriptionText }}</td>
                            <td style='padding: 5px 10px; text-align: right; vertical-align: top; width: 80px;'>{{ qtyText }}</td>
                            <td style='padding: 5px 10px; text-align: right; vertical-align: top; width: 80px;'>{{ unitPriceText }}</td>
                            {% if amountIdx != nil %}
                            <td style='padding: 5px 10px; text-align: right; vertical-align: top; width: 80px;'>{{ amountText }}</td>
                            {% endif %}
                            <td style='padding: 5px 10px; text-align: right; vertical-align: top; width: 50px;'>{{ taxAmountText }}</td>
                            <td style='padding: 5px 10px; text-align: right; vertical-align: top; width: 100px;'>{{ totalText }}</td>
                        </tr>
                    {% endfor %}
                    <tr style='border-bottom: 1px solid #ddd;'>
                        <td colspan='99'> </td>
                    </tr>
                </tbody>
            </table>
            {% assign amountInWords = '' %}
            {% for cf in custom_fields %}
                {% if cf.label == 'Total amount in words' %}
                    {% assign amountInWords = cf.text %}
                {% endif %}
            {% endfor %}
            <table style='width: 100%;'>
                <tbody>
                    {% assign rowCount = 0 %}
                    {% for total in table.totals %}
                        {% unless total.amount == -1 and total.label == 'Sub-total' %}
                        {% assign rowCount = forloop.index %}
                        {% endunless %}
                    {% endfor %}
                    {% for total in table.totals %}
                       {% unless total.amount == -1 and total.label == 'Sub-total' %}
                        <tr>
                            {% if forloop.first %}
                                <td rowspan='{{ rowCount }}' colspan='4' style='padding: 5px 10px; text-align: left; border-top: 1px solid #dddddd; background-color: #f8f9fa;'>
                                    {% if amountInWords != '' %}<p><strong>Total amount in words: </strong><br /><i>{{ amountInWords }}</i></p>{% endif %}
                                </td>
                            {% endif %}
                            {% assign textArray = total.text | split: ' ' %}
                            {% assign firstIndex = textArray | first %}
                            {% assign lastIndex = textArray | last %}
                            <td colspan='2' style='padding: 5px 10px; text-align: right; {% if forloop.first %} border-top: 1px solid #dddddd; {% endif %} background-color: #f8f9fa;'>
                                <span style='{% if total.emphasis %} font-weight: bold; {% else %} font-weight: normal; {% endif %}'>
                                    {% if total.label == 'Sub-total' %}
                                        Total (excl. tax)
                                    {% elsif total.label == 'Total' %}
                                        Invoice total, {{ firstIndex }}
                                    {% elsif total.class == 'taxAmount' %}
                                        Total {{ total.label }}
                                    {% else %}
                                        {{ total.label }}
                                    {% endif %}
                                </span>
                            </td>
                            <td style='padding: 5px 10px; text-align: right; {% if forloop.first %} border-top: 1px solid #dddddd; {% endif %} background-color: #f8f9fa; width: 100px;'>
                                <span style='{% if total.emphasis %} font-weight: bold; {% else %} font-weight: normal; {% endif %}'>
                                    {{ lastIndex }}
                                </span>
                            </td>
                        </tr>
                        {% endunless %}
                    {% endfor %}
                </tbody>
            </table>
        </td>
    </tr>
        <tr>
            <td colspan='99'>
                {% if emphasis.text != null and emphasis.positive %}
                <div style='text-align: center; margin-top: 40px'><span style='color: #006400; border-width: 5px; border-color: #006400; border-style: solid; padding: 10px; font-size: 20px'>{{ emphasis.text | upcase }}</span></div>
                {% endif %}
                {% if emphasis.text != null and emphasis.negative %}
                <div style='text-align: center; margin-top: 40px'><span style='color: #FF0000; border-width: 5px; border-color: #FF0000; border-style: solid; padding: 10px; font-size: 20px'>{{ emphasis.text | upcase }}</span></div>
                {% endif %}
            </td>
        </tr>
		<tr>
            <td colspan='99'>
				 
			</td>
        </tr>
</table>
